(window.webpackJsonp=window.webpackJsonp||[]).push([[47],{466:function(v,_,p){"use strict";p.r(_);var t=p(8),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"关于-327-国债事件的10个问题"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#关于-327-国债事件的10个问题"}},[v._v("#")]),v._v(" 关于“327”国债事件的10个问题")]),v._v(" "),_("h2",{attrs:{id:"背景"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#背景"}},[v._v("#")]),v._v(" 背景")]),v._v(" "),_("p",[v._v("1993年随着通货膨胀程度进一步加深，全面的宏观调控展开。市场利率上行使得国债券价格下降甚至低于发行价，新发行的债券无人问津，原有通过承购包销销售债券和利率市场化改革的方法难以让国债顺利发行。国债市场化改革陷入停滞，为完成国债发行任务，国债发行恢复为原先的组织认购和行政摊派，政府开始对银行存款和国债进行贴息和保值贴补。")]),v._v(" "),_("p",[v._v("针对93年国债，其3年期和5年期利率由12.52%和14.06%调高至13.96%和15.86%，对3年期和5年期国债进行保值，保值贴补率由兑付时人民银行公布的保值补率计算。市场上国债保值和非保值的不同状态。同时保值公告在92年发行国债上仅作保值决定，具体保值方式尚未披露，国债价格的不确定性加大。")]),v._v(" "),_("p",[v._v("“327国债”指1992年发行1995年6月到期兑付的3年期国库券，由于市场对于政府是否会对327国债进行贴息和保值贴补存在分歧，多空双方代表“中经开”和“万国证券”针对该标的进行炒作博弈，分别建立多仓和空仓，在市场上发生激烈对抗，最终政府进行保值贴补，引得国债期货价格大幅上升。期货市场出现“多逼空”行情。")]),v._v(" "),_("h2",{attrs:{id:"q-a"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#q-a"}},[v._v("#")]),v._v(" Q&A")]),v._v(" "),_("p",[_("strong",[v._v("Q1：1993年决定对国债进行贴息和保值贴补的原因？")])]),v._v(" "),_("p",[v._v("1993年随着通货膨胀程度进一步加深，全面的宏观调控展开。市场利率上行使得国债券价格下降甚至低于发行价，新发行的债券无人问津，原有通过承购包销销售债券和利率市场化改革的方法难以让国债顺利发行。国债市场化改革陷入停滞，为完成国债发行任务，国债发行恢复为原先的组织认购和行政摊派，政府开始对银行存款和国债进行贴息和保值贴补。")]),v._v(" "),_("p",[_("strong",[v._v("Q2：政府对国债进行贴息和保值贴补的决策对国债和国债期货市场造成什么样的影响？")])]),v._v(" "),_("p",[v._v("针对93年国债，其3年期和5年期利率由12.52%和14.06%调高至13.96%和15.86%，对3年期和5年期国债进行保值，保值贴补率由兑付时人民银行公布的保值补率计算。市场上国债保值和非保值的不同状态。同时保值公告在92年发行国债上仅作保值决定，具体保值方式尚未披露，国债价格的不确定性加大。")]),v._v(" "),_("p",[v._v("国债收益率由原先的固定利率决定转变为由贴息率和保值贴补率决定，国债兑付收益率不确定性大大增加，相关的国债期货炒作空间加大。327事件中以中经开和万国为代表的多空双方则围绕是否会对1992年发行1995年6月到期兑付的3年期国债进行保值贴补建立多仓和空仓，在市场上发生激烈对抗，最终政府进行保值贴补，引得国债期货价格大幅上升。期货市场出现“多逼空”行情。")]),v._v(" "),_("p",[_("strong",[v._v("Q3：中经开做多缘由？")])]),v._v(" "),_("p",[v._v("① 国家政策的需要：通过做多活跃国债一级市场，以二级市场带动一级市场，更好顺利地发行国债，同时避免国债破净")]),v._v(" "),_("p",[v._v("② 国债市场的行情现实：银行资产金融结构调整下对国债存在需求；市场更加完善下，国债投资价值逐渐被大家认识")]),v._v(" "),_("p",[_("strong",[v._v("Q4：万国证券在327国债期货上的多空决策是怎么样的？")])]),v._v(" "),_("p",[v._v("① 判断国内通胀得到基本控制，市场将会有新券上市，同时受保值贴补率不断上升刺激，基本面上分析当前327合约价格已经偏高，万国证券开始开设空仓，春节前持空仓量达到40万口")]),v._v(" "),_("p",[v._v("② 国内通胀并未被控制，保值贴补率连创新高，总部判断下月保值贴补率将回落，没有选择平仓操作，2月中旬空仓量达到87万口")]),v._v(" "),_("p",[v._v("③ 操作策略转变为“不再开新仓，逐渐平仓出局”。但17日仍新开空仓42万口，多空操作决策已经偏离基本面分析，同时放弃了期货的对冲和风险控制机制，企图以资金实力决定期货操作输赢")]),v._v(" "),_("p",[v._v("④ 至22日，万国在采取积极减仓策略下，总空仓量仍达到144万口，同时其协议平仓未果")]),v._v(" "),_("p",[v._v("⑤ 23日，万国采取高价位开空仓来阻止价格上升，空仓量增加40余万口；收盘前万国砸出2070万口空单将327合约价格锁定在147.9元")]),v._v(" "),_("p",[_("strong",[v._v("Q5：在出现巨额亏损的状况下，万国证券采取的自救措施为什么没有成效？")])]),v._v(" "),_("p",[v._v("① 万国企图以资金实力决定期货操作输赢，通过新开空仓的方式企图使多方爆仓，但多头行情已经确立，市场多头资金能够承接万国的空单，同时空单数量已经超过了92年3月现券总量，空方交割兑付使会导致期货价格上升。新开空单打压价格的决策反倒使万国陷入更大的危机")]),v._v(" "),_("p",[v._v("② 18日会议拟定的协议平仓、转让合约、寻求减仓的基本解决方案实施不顺，犹豫迟疑中万国仍旧保持做空形势，多方上拉价位打击空方减仓，即使万国能够部分平仓，其也为打压价格而开更多的新空单付出更大的代价")]),v._v(" "),_("p",[v._v("③ 23日最后8分钟交易遭到取消，万国最后的努力失败，万国证券濒临破产")]),v._v(" "),_("p",[_("strong",[v._v("Q6：“327事件”中辽国发充当的角色是什么？")])]),v._v(" "),_("p",[v._v("327事件初，辽国发通过收购控制金融机构分散从事国债期货业务，与万国证券合仓做空327合约；后辽国发选择“空翻多”降低亏损，将空单移仓到无锡国泰规避债务，通过M16席位透支开设空仓，致使其在327合约上违约交收形成巨额债务。辽国发债务无法得到清偿，证券结算系统正常结算交收受到严重威胁，在一定程度上制约了上海证券市场的发展。")]),v._v(" "),_("p",[_("strong",[v._v("Q7：上交所尉文渊对最后的8分钟交易结果的考虑？")])]),v._v(" "),_("p",[v._v("①  万国证券违规交易：上海证券交易所在国债期货交易规则上现行保证金控制和持仓限额两大机制，其中保证金控制方面采取“逐日盯市”而非“逐笔盯市”；虽有持仓限额，但市场上借席位开仓已成风气；国债期货交易缺乏涨跌停板制度。万国证券最后8分钟的空单没有足够的保证金，同时也大大超出了上交所的持仓限额规定")]),v._v(" "),_("p",[v._v("②  避免市场发生系统性风险：如若取消最后的交易，万国将面临破产，其后可能引发挤兑，波及其他证券公司进而冲击证券整个市场")]),v._v(" "),_("p",[_("strong",[v._v("Q8：327国债事件暴露出来当时国债期货试点缺陷有哪些？")])]),v._v(" "),_("p",[v._v("① 期货市场需要与现货市场相匹配，当时国内债券市场规模较小，市场对利用国债期货进行套期保值的需求不足；在现货不满足期货交割需求时容易出现市场操纵以及极端投机。327事件中“多逼空”是其特征，空方大量空单，即使交割也没有足够的国债现货进行交收")]),v._v(" "),_("p",[v._v("② 在交易制度上存在设计缺陷，进行国债期货交易的保证金比例低，这为利用资金优势操纵市场提供了可能，容易出现逼空行情；“逐日盯市”的保证金控制使得每日结算时可能以及出现了巨大的资金亏损，不利于证券市场结算，极端下可能造成市场崩溃；没有实行涨跌停板制度和大户报告制度，在市场行情波动剧烈情况下，没有涨跌停板来限制期货价格，同时针对合约持仓大户没有合适的报告制度，极端情况下少数大户持有大量合约容易造成合约风险集聚；虽然有持仓限额，但是没有有效的制度法规制约移仓，借席位等行为，容易出现超仓")]),v._v(" "),_("p",[v._v("③ 信息披露不规范，国债期货成为“消息市”牺牲品，对327国债行情影响关键的保值贴补率等信息在还未披露时就被市场上部分投资者知晓，其他投资者消息滞缓，信息不对称下多空双方分歧严重，致使327国债引发严重后果")]),v._v(" "),_("p",[_("strong",[v._v("Q9：试点时期，国内国债期货市场与其他国家国债期货市场存在多大的差距，为什么国内试行国债期货与理想存在巨大差距，最终导致国债期货夭折？")])]),v._v(" "),_("p",[v._v("① 国内国债现货市场规模不大，投机氛围浓重；外国国债期货发展时期长，市场规模大，合约交易活跃，国债现货市场规模、品种、流动性等方面都要明显优于当时国内市场")]),v._v(" "),_("p",[v._v("② 利率市场化机制不完善，政府行政控制利率变动不能及时适应市场；国外市场化程度深，部分国家高度市场化，利率变动受到行政干预程度小，对国债期现货价格影响不大")]),v._v(" "),_("p",[v._v("③ 国债期货市场法律法规不完善，国债期货长期在无法可依的市场下运行，同时交易所监管职能没有得到有效履行；如美国国债期货市场，通过“三级管理体制”将政府监管机构、行业协会组织和期货交易所自我监管三者结合，强调政府干预并通过加强立法来管理期货市场。")]),v._v(" "),_("p",[v._v("④ 国内国债期货保证金和标的物的合约设计上存在缺陷，保证金按照票面价值收取，同时收取比例过低，市场风险易被放大，期货合约标的为某一年发行国债或某一特定国债，交割品种、规模有限；国际上国债期货按照合约市场价格一定比例收取，在市场价格上升时保证金收取金额也会随之上升，利于控制风险，国际多采用最便宜可交割债券进行交割，克服单一交割品种规模有限问题，在一定程度避免市场操纵行为")]),v._v(" "),_("p",[_("strong",[v._v("Q10：十八年后国债期货重启，与当初相比，国内国债期货的发展基础有哪些改变？")])]),v._v(" "),_("p",[v._v("① 国债市场化发行机制成熟，二级市场交易活跃，国债规模进一步扩大，期货市场与现货市场相匹配")]),v._v(" "),_("p",[v._v("② 利率市场化程度加深，行政干预影响小")]),v._v(" "),_("p",[v._v("③ 国内期货市场相关法律法规完善，监管体系改革，交易受到严格监管")]),v._v(" "),_("p",[v._v("④ 合约设计与运行上，国债保证金比例提高，以合约价格为基础是收取；交割品种扩大到其他国债上，不再局限于单一品种；实行涨跌停板制度和大户报告制度")]),v._v(" "),_("p",[v._v("本文回答据 陆一《中国赌金者——327事件始末》及网络资料整理")])])}),[],!1,null,null,null);_.default=s.exports}}]);